// Material UI stuffs
import Button from '@mui/material/Button';
import Container from '@mui/material/Container'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Components
import Appbar from './components/Appbar';
import InputDisplay from './components/InputDisplay';

// Imports
import {useState, useEffect} from 'react'
import axios from 'axios'
import { Input } from '@mui/material';

const api_url = 'https://vb-react-exam.netlify.app/api/form'

function App() {

  useEffect(() => {
    fetchData()
  }, [])

  const [apiData, SetApiData] = useState([])
  const stuff = false;

  const fetchData = () => {
    axios.get(api_url)
          .then(res => {
            const data = res.data.data
            SetApiData(data)
          })
          .catch(err => {
            console.log(err)
          })
  }

  const DisplayData = () => {
    console.log(apiData)
  }

  return (
    <div>
      <Container maxWidth="lg">
          <Appbar/>
          <Box sx={{ mt: 3}}>

              <Typography sx={{mb:0}} variant="h2" component="div" gutterBottom>
                  Welcome
              </Typography>

              <Typography sx={{mb:5}} variant="subtitle1" component="div" gutterBottom>
                  A test project by Dran.
              </Typography>



              <Box sx={{mt:3}}>
                {apiData ?
                  <div>
                    {apiData.map(data => (
                      <div>
                          <InputDisplay type={data.type} value={data.value} name={data.fieldName} options={data.options} />
                      </div>
                    ))}
                  </div>
                : null}
              </Box>







              {/* <Box sx={{mt:3}}>
                  <Button onClick={DisplayData} sx={{}} variant="contained">Hello World</Button>
              </Box> */}


          </Box>
      </Container>

    </div>
  );
}

export default App;
