import React from 'react'
import Box from '@mui/material/Box'
import TextField from '@mui/material/TextField'
import { MenuItem } from '@mui/material'

import {useState} from 'react'
import { Button } from '@mui/material'

function InputDisplay({data}) {
    
    // Fixes the FieldNames
    const FixNames = (string) => {
        let word = string.replace(/([A-Z])/g, ' $1').trim();
        word = word.toLowerCase().replace(/\b[a-z]/g, function(letter) {
            return letter.toUpperCase();
        });
        return word
    }

    const [dataValue, setDataValue] = useState(data.value)


    const DisplayStuff = () => {
        console.log(dataValue)
    }


    return (
        <div>
            <Box sx={{mb:3, width:1, border:0}}>
                {/* Name: {data.fieldName} <br/>
                Type: {data.type} <br/>
                Value: {data.value}

                {data.options ?
                    <div>
                        Options:
                        {data.options.map(option => (
                            <div>- {option} <br/></div>
                        ))}
                    </div>
                : null} */}


                {/* TEXT FIELD */}
                {data.type != 'multiline' && data.type != 'select' ?
                    <div>
                        <TextField
                            id="text-input"
                            label={FixNames(data.fieldName)}
                            defaultValue={dataValue}
                            sx ={{width:1}}
                            onChange={e => setDataValue(e.target.value)}
                            type={data.type}
                        />
                    </div>
                : null}

                {/* SELECT FIELD */}
                {data.type == 'select' ?
                    <div>
                        <TextField
                            id="select-input"
                            select
                            label={FixNames(data.fieldName)}
                            value={data.value}
                            fullWidth
                            onChange={e => setDataValue(e.target.value)}
                        >
                            {data.options.map(option => (
                                <MenuItem key={option} value={option}>
                                    {option}
                                </MenuItem>
                            ))}
                        </TextField>
                    </div>
                : null}

                {/* MULTILINE FIELD */}
                {data.type = 'multiline' ?
                    <div>
                        <TextField
                            id="text-input"
                            label={FixNames(data.fieldName)}
                            defaultValue={dataValue}
                            sx ={{width:1}}
                            onChange={e => setDataValue(e.target.value)}
                            type={data.type}
                        />
                    </div>
                : null}
                
               
                {/* <Button onClick={DisplayStuff}>Display</Button> */}
            </Box>
        </div>
    )
}


export default InputDisplay
