import React from 'react'
import Box from '@mui/material/Box'
import TextField from '@mui/material/TextField'

import {useState} from 'react'

function InputDisplay({data}) {
    
    // Fixes the FieldNames
    const FixNames = (string) => {
        let word = string.replace(/([A-Z])/g, ' $1').trim();
        word = word.toLowerCase().replace(/\b[a-z]/g, function(letter) {
            return letter.toUpperCase();
        });
        return word
    }

    const [dataValue, setDataValue] = useState(data.value)

    return (
        <div>
            <Box sx={{mb:3, width:1, border:0}}>
                {/* Name: {data.fieldName} <br/>
                Type: {data.type} <br/>
                Value: {data.value}

                {data.options ?
                    <div>
                        Options:
                        {data.options.map(option => (
                            <div>- {option} <br/></div>
                        ))}
                    </div>
                : null} */}


                {/* TEXT FIELD */}
                <TextField
                    id="outlined-required"
                    label={FixNames(data.fieldName)}
                    defaultValue={dataValue}
                    sx ={{width:1}}
                    onChange={e => setDataValue(e.target.value)}
                />
            </Box>
        </div>
    )
}


export default InputDisplay
