// Material UI stuffs
import Button from '@mui/material/Button';
import Container from '@mui/material/Container'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid'
import Alert from '@mui/material/Alert'

// Components
import Appbar from './components/Appbar';
import InputDisplay from './components/InputDisplay';

// Imports
import {useState, useEffect} from 'react'
import axios from 'axios'

const api_url = 'https://vb-react-exam.netlify.app/api/form'

function App() {

  // Use Effect
  useEffect(() => {
    fetchData()
  }, [])

  // States
  const [apiData, SetApiData] = useState([])
  const [isSubmitting, setIsSubmitting] = useState(true)

  // Fetch Data from API
  const fetchData = () => {
    axios.get(api_url)
          .then(res => {
            const data = res.data.data
            SetApiData(data)
          })
          .catch(err => {
            console.log(err)
          })
  }

  // Track changes on values by user
  const ChangeValue = (key, value) => {
    const found = apiData.find(element => element.fieldName == key)
    found.value = value
  }

  // Handles submition
  const SubmitData = () => {

    // Creates a new object to be submitted
    const newdata = {}
    const AppendData = (item) => {
      newdata[item.fieldName] = item.value
    }
    apiData.forEach(AppendData)

    // Submit post
    axios.post(api_url, newdata)
          .then(res => {
            console.log(res.data)
          })
          .catch(err => {
            console.log(err)
          })
  }

  return (
    <div>
      <Appbar/>

      <Container maxWidth="md">    
          <Box sx={{ mt: 3}}>

              <Typography sx={{mb:0}} variant="h2" component="div" gutterBottom>
                  Welcome
              </Typography>

              <Typography sx={{mb:5}} variant="subtitle1" component="div" gutterBottom>
                  A test project by Dran.
              </Typography>



              <Box sx={{mt:3}}>
                {apiData ?
                  <div>
                    {apiData.map(data => (
                      <div key={data.value}>
                          <InputDisplay data={data} ChangeValue={ChangeValue} isSubmitting = {isSubmitting} />
                      </div>
                    ))}
                  </div>
                : null}
              </Box>




              {/* SUBMIT BUTTON */}
              <Grid
                container
                spacing={0}
                direction="column"
                alignItems="center"
                justifyContent="center"
              >

                <Grid item xs={3}>
                  <Button onClick={SubmitData} variant="contained">Submit</Button> 
                </Grid>   

              </Grid> 


              <Alert sx={{mt:3}} severity="success">This is a success alert â€” check it out!</Alert>


              {/* <Box sx={{mt:3}}>
                  <Button onClick={handleOpen} sx={{}} variant="contained">TEST</Button>
              </Box> */}



          </Box>
      </Container>

    </div>
  );
}

export default App;
