import Button from '@mui/material/Button';
import Container from '@mui/material/Container'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

import Appbar from './components/Appbar';

import {useState, useEffect} from 'react'
import axios from 'axios'

const api_url = 'https://vb-react-exam.netlify.app/api/form'

function App() {

  useEffect(() => {
    fetchData()
  }, [])

  const [apiData, SetApiData] = useState([{}, {}])
  const stuff = true;

  const fetchData = () => {
    axios.get(api_url)
          .then(res => {
            const data = res.data.data
            SetApiData(data)

            console.log(apiData)
          })
          .catch(err => {
            console.log(err)
          })
  }

  const DisplayData = () => {
    console.log(apiData)
  }

  return (
    <div>
      <Container maxWidth="lg">
          {/* <Appbar/> */}
          <Box sx={{ mt: 5}}>

              <Typography sx={{mb:0}} variant="h2" component="div" gutterBottom>
                  Welcome
              </Typography>

              <Typography sx={{}} variant="subtitle1" component="div" gutterBottom>
                  A test project by Dran.
              </Typography>

              {stuff ? 
                <div>

                </div>
              : null }


              {apiData.map(data => (
                <div>
                    {data.fieldName}: {data.value}
                </div>
              ))}


              <Box sx={{mt:3}}>
                  <Button onClick={DisplayData} sx={{}} variant="contained">Hello World</Button>
              </Box>


          </Box>
      </Container>

    </div>
  );
}

export default App;
